module.exports = function (path, log) {
    var request = require('request');
    var module = {};

    //settings
    module.name = "sample-rokunetflix";
    module.mac = "74:CX:XX:XX:XX:XX";
    module.last_triggered = null;
    module.ip = "10.20.30.199";

    //execute function
    module.execute = function() {
        request.post({
            url: "http://" + module.ip + ":8060/launch/12"
        }, function(error, response, body){
            log(module.name, "Response: " + response.statusCode);
            if (response.statusCode == 200) { log(module.name, "Netflix engaged."); }
            else { log(module.name, "Failed to launch netflix."); }

        });
    };

    return module;
};

